public class SistemaMedida {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String sigla = "M";
		
		switch(sigla) {
		
		case "P":{
			System.out.println("PEQUENO");
			break;
		}
		
		case "M":{
			System.out.println("MAIOR");
			break;
		}
		}

	}

}
